args.aiStr:
{
    init: function (ccblock) {

        // component = this.get(tag)
        // this.each(function(component){ ... });
        
        this.mover = this.make_controller(
            'movement',
            {
                comp: ccblock,
                radius: 5,
                charge: true,
                hinge: true
            }
        );
        
        this.sensor = this.make_sensor(
            'radius',
            {
                comp: ccblock,
                angle: [ -Math.PI/6, Math.PI/6 ],
                distance: [ 0, 45 ],
                enemy: true
            }
        )
        
        this.sensor2 = this.make_sensor(
            'radius',
            {
                comp: ccblock,
                distance: [ 0, 15 ],
                enemy: true
            }
        )        

        this.evade = false;
        this.time = 0.0;
        this.ccblock = ccblock;

    },

    update: function (dt, keyDown) {
        
        this.time += dt;
        
        if (this.time > 12.0 && this.sensor2.first && this.player_cc().p()) {
            var self = this;
            var ep = this.player_cc().p().clone();
            var mp = this.ccblock.p();
            ep.x -= mp.x;
            ep.y -= mp.y;
            var len = Math.lenVec2(ep);
            ep.x = -ep.x/len + Math.random()*0.25 - 0.125;
            ep.y = -ep.y/len + Math.random()*0.25 - 0.125;
            ep.x *= 6;
            ep.y *= 6;
            ep.x += mp.x;
            ep.y += mp.y;
            this.sub(7, function(dt, keyDown, t){
                self.mover.moveTo(
                    ep,
                    keyDown,
                    BSWG.KEY.LEFT, BSWG.KEY.RIGHT,
                    BSWG.KEY.UP, BSWG.KEY.DOWN
                );
                keyDown[BSWG.KEY.SPACE] = true;
            });
            this.time = 0.0;
        }
        
        var tp = this.targetp || this.player_cc().p().clone();
        
        this.mover.moveTo(
            tp,
            keyDown,
            BSWG.KEY.LEFT, BSWG.KEY.RIGHT,
            BSWG.KEY.UP, BSWG.KEY.DOWN
        );

        if (this.sensor.first) {
            keyDown[BSWG.KEY.SPACE] = true;
        }
        
        if (this.sensor2.first) {
            keyDown[BSWG.KEY.DOWN] = true;
            keyDown[BSWG.KEY.UP] = false;
        }

    }
}
